import React, { Component} from 'react';
import anime from 'animejs'
//* CSS
import classes from './BlackSlide.module.css';
//*Images
import LeftChevron from '../..//static/images/icons/chevronLeft.png';
import RightChevron from '../..//static/images/icons/chevronRight.png';

class BlackSlide extends Component{

    constructor(props){
        super(props);
        this.nYearWrapper = React.createRef();
        this.state={
            counts: 0,
        }
    }
    // useEffect(() => {
    //     effect
    //     return () => {
    //         cleanup
    //     };
    // }, [input]);
    moveLeft=()=>{
        console.log('running')
        this.setState({counts: this.state.counts-1,})

        anime.timeline({loop:false})
        .add({
            targets: this.nYearWrapper.current,
            translateX:-100,
            duration:1500,
            easing: 'easeInOutExpo',
        })
    }
    render(){
    return (
        <div className={classes.BlackSlideContainer}>
            <img src={LeftChevron} className={classes.LeftChevron} alt='Left side chevron' onClick={this.moveLeft}/>
            <img src={RightChevron} className={classes.RightChevron} alt='Right side chevron'/>
            <div className={classes.BlackSlideWrapper}>
                <div className={classes.nYear} ref={this.nYearWrapper}>
                    <div className={classes.SectionsWrapper}>
                        <section>
                            <h6>Octubre</h6>
                            <p>Creación del grupo de estudiantes. </p>
                        </section>
                        <section>
                            <h6>Noviembre</h6>
                            <p>Presentación del grupo en la Facultad de Mecánica - ESPOCH</p>
                        </section>
                        <section>
                            <h6>Diciembre</h6>
                            <p>Unión al grupo de investigación GIDETER - ESPOCH</p>
                        </section> 
                    </div>
                    <div className={classes.BottomWrapper}>
                        <h4>2019</h4>
                    </div>
                </div>
                <div className={classes.nYear}>
                    <div className={classes.SectionsWrapper}>
                        <section>
                            <h6>Enero</h6>
                            <p>Creación del grupo de estudiantes. </p>
                        </section>
                        <section>
                            <h6>Febrero</h6>
                            <p>Presentación del grupo en la Facultad de Mecánica - ESPOCH</p>
                        </section>
                        <section>
                            <h6>Marzo</h6>
                            <p>Unión al grupo de investigación GIDETER - ESPOCH</p>
                        </section> 
                    </div>
                    <div className={classes.BottomWrapper}>
                        <h4>2020</h4>
                    </div>
                </div>
            </div>
        </div> 

    );
    }

}

export default BlackSlide;
